import React, { useState } from 'react'

const App = () => {

  const[click, setClick] = useState(5);

  const handleClick = () => {
    if (click>5) {
      setClick(click-1)
    }
  }

  return (
    <div>
        <h1>Value: {click} </h1>
        <button onClick={()=> setClick(click+1)}>Increment</button>
        <button onClick={handleClick}>Decrement</button>
     </div>
  )
}

export default App
