import React, { useState } from 'react'

const App = () => {

  const[click, setClick] = useState(0);

  return (
    <div>
        <h1>Value: {click} </h1>
        <button onClick={()=> setClick(click+1)}>Increment</button>
        <button onClick={()=> setClick(click-1)}>Decrement</button>
     </div>
  )
}

export default App
